task usercontrol()
{
	int sped = 1;
	while(true)
	{
		if(vexRT[Btn8D] && sped == 1){
			sped = 2;
			} else if(vexRT[Btn8D] && sped == 2) {
			sped = 1;
		}

		motor(fRbR) = vexRT[Ch3] - (vexRT[Ch4] * (0.25 * sped));
		motor(fLbL) = vexRT[Ch3] + (vexRT[Ch4] * (0.25 * sped));

		motor(armLeft) = -vexRT[Ch2];
		motor(armRight) = -vexRT[Ch2];
	}
}
